import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const surahList = [
  { number: 1, name: 'Al-Fatihah' },
  { number: 2, name: 'Al-Baqarah' },
  { number: 3, name: 'Al-Imran' },
  // Add all other Surahs here
  { number: 114, name: 'An-Nas' },
];

const MainContent = () => {
  const [userData, setUserData] = useState(null);
  const [newGoal, setNewGoal] = useState({ ayahs_per_day: '', minutes_per_day: '' });
  const [ayahsRead, setAyahsRead] = useState('');

  // Fetch user data on component mount
  useEffect(() => {
    const userId = localStorage.getItem('user_id');
    if (!userId) {
      window.location.href = '/login';
      return;
    }

    fetch(`http://localhost:5000/main?user_id=${userId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.error) {
          alert('No user data found');
        } else {
          setUserData(data);
        }
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  return (
    <div className="main-content">
      {userData ? (
        <div>
          <h1>Your Progress</h1>
          <p>Ayahs Per Day: {userData.ayahs_per_day || 'N/A'}</p>
          <p>Minutes Per Day: {userData.minutes_per_day || 'N/A'}</p>
          <p>Ayahs Read: {userData.ayahs_read || 0}</p>

          {/* Display Surahs */}
          <div className="surah-cards">
            {surahList.map((surah) => (
              <Link to={`/surah/${surah.number}`} key={surah.number} className="surah-card">
                {surah.name}
              </Link>
            ))}
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default MainContent;
