import React, { useEffect, useState } from 'react';
import SurahList from './SurahList'; // Import SurahList

const MainContent = () => {
  const [userData, setUserData] = useState(null);
  const [newGoal, setNewGoal] = useState({ ayahs_per_day: '', minutes_per_day: '' });
  const [ayahsRead, setAyahsRead] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const userId = localStorage.getItem('user_id');
    if (!userId) {
      window.location.href = '/login';
      return;
    }

    fetch(`http://localhost:5000/main?user_id=${userId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.error) {
          alert('No user data found');
        } else {
          setUserData(data);
        }
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  const handleGoalSubmit = (e) => {
    e.preventDefault();
    const userId = localStorage.getItem('user_id');
    fetch('http://localhost:5000/goals', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...newGoal, user_id: userId }),
    })
      .then(() => fetch(`http://localhost:5000/main?user_id=${userId}`))
      .then((response) => response.json())
      .then((data) => setUserData(data))
      .catch((error) => console.error('Error setting or updating goals:', error));
  };

  const handleProgressUpdate = (e) => {
    e.preventDefault();
    const userId = localStorage.getItem('user_id');
    fetch('http://localhost:5000/progress', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ayahs_read: parseInt(ayahsRead, 10), user_id: userId }),
    })
      .then(() => {
        setUserData((prevData) => ({
          ...prevData,
          ayahs_read: (prevData.ayahs_read || 0) + parseInt(ayahsRead, 10),
        }));
      })
      .catch((error) => console.error('Error updating progress:', error));
  };

  return (
    <div className="main-content">
      {userData ? (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', width: '100%' }}>
          <div className="progress-section">
            <h2>Your Progress</h2>
            <p>Ayahs Per Day: {userData.ayahs_per_day || 'N/A'}</p>
            <p>Minutes Per Day: {userData.minutes_per_day || 'N/A'}</p>
            <p>Ayahs Read: {userData.ayahs_read || 0}</p>

            <form onSubmit={handleGoalSubmit}>
              <div className="input-field">
                <input
                  type="number"
                  placeholder="Ayahs per day"
                  value={newGoal.ayahs_per_day}
                  onChange={(e) => setNewGoal({ ...newGoal, ayahs_per_day: e.target.value })}
                />
              </div>
              <div className="input-field">
                <input
                  type="number"
                  placeholder="Minutes per day"
                  value={newGoal.minutes_per_day}
                  onChange={(e) => setNewGoal({ ...newGoal, minutes_per_day: e.target.value })}
                />
              </div>
              <button type="submit">Set or Update Goals</button>
            </form>

            <form onSubmit={handleProgressUpdate}>
              <div className="input-field">
                <input
                  type="number"
                  placeholder="Ayahs read"
                  value={ayahsRead}
                  onChange={(e) => setAyahsRead(e.target.value)}
                />
              </div>
              <button type="submit">Update Progress</button>
            </form>
          </div>

          <div className="surah-cards">
            <button className="surah-card">Surah Al-Fatihah</button>
            <button className="surah-card">Surah Al-Baqarah</button>
            <button className="surah-card">Surah Al-Imran</button>
            {/* Add more Surah buttons */}
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );  
};


export default MainContent;
