import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../App.css';

const SurahDetail = () => {
  const { surahNumber } = useParams(); // Get the Surah number from the URL
  const [surahName, setSurahName] = useState(''); // Arabic Surah name
  const [ayahs, setAyahs] = useState([]); // Ayahs of the Surah
  const [translations, setTranslations] = useState([]); // Translations of Ayahs
  const [translationLang, setTranslationLang] = useState('en'); // Default language
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch Surah Name and Ayahs
    fetch(`http://localhost:5000/surah/${surahNumber}`)
      .then(response => response.json())
      .then(data => {
        if (data.error) {
          setError(data.error);
        } else {
          setAyahs(data.Ayahs);
          setSurahName(data.Surah_Name || ''); // Extract Surah name in Arabic
        }
      })
      .catch(() => setError('Error fetching Ayahs.'));

    // Fetch Translations
    fetch(`http://localhost:5000/translation/${translationLang}/${surahNumber}`)
      .then(response => response.json())
      .then(data => {
        if (data.error) {
          setError(data.error);
        } else {
          setTranslations(data);
        }
      })
      .catch(() => setError('Error fetching translations.'));
  }, [surahNumber, translationLang]); // Refetch when surahNumber or language changes

  // Handle Language Change
  const handleLanguageChange = (lang) => {
    setTranslationLang(lang);
  };

  return (
    <div className="surah-detail">
      {/* Display Arabic name of the Surah */}
      <h1>Surah {surahName} </h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div className="ayah-container">
        {ayahs.map((ayah, index) => (
          <div key={index} className="ayah-card">
            <p>
              <strong>Ayah {ayah.Ayah_Number}: </strong>
              {ayah.Ayah_Text}
            </p>
            <div className="translation">
              <p>
                <strong>Translation: </strong>
                {translations[index]?.Ayah_Translation || 'Loading translation...'}
              </p>
              <button
                className="dropdown-toggle"
                onClick={() => handleLanguageChange('ur')} // Example of changing to Urdu
              >
                Change Translation Language
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="navigation-buttons">
        <button
          disabled={surahNumber <= 1} // Disable Prev button for the first Surah
          onClick={() => navigate(`/surah/${parseInt(surahNumber) - 1}`)}
        >
          Previous
        </button>
        <button
          onClick={() => navigate(`/surah/${parseInt(surahNumber) + 1}`)}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default SurahDetail;
