import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './MainContent.css';

const MainContent = () => {
  const [userData, setUserData] = useState(null);
  const [newGoal, setNewGoal] = useState('');
  const [ayahsRead, setAyahsRead] = useState('');
  const [progressPercentage, setProgressPercentage] = useState(0);

  const surahNames = [
    "Al-Fatihah", "Al-Baqarah", "Al-Imran", "An-Nisa", "Al-Maidah", "Al-An'am", "Al-A'raf",
    "Al-Anfal", "At-Tawbah", "Yunus", "Hud", "Yusuf", "Ar-Ra'd", "Ibrahim", "Al-Hijr",
    "An-Nahl", "Al-Isra", "Al-Kahf", "Maryam", "Taha", "Al-Anbiya", "Al-Hajj", "Al-Mu'minun",
    "An-Nur", "Al-Furqan", "Ash-Shu'ara", "An-Naml", "Al-Qasas", "Al-Ankabut", "Ar-Rum",
    "Luqman", "As-Sajda", "Al-Ahzab", "Saba", "Fatir", "Ya-Sin", "As-Saffat", "Sad",
    "Az-Zumar", "Ghafir", "Fussilat", "Ash-Shura", "Az-Zukhruf", "Ad-Dukhan", "Al-Jathiya",
    "Al-Ahqaf", "Muhammad", "Al-Fath", "Al-Hujurat", "Qaf", "Adh-Dhariyat", "At-Tur",
    "An-Najm", "Al-Qamar", "Ar-Rahman", "Al-Waqia", "Al-Hadid", "Al-Mujadila", "Al-Hashr",
    "Al-Mumtahanah", "As-Saff", "Al-Jumu'a", "Al-Munafiqun", "At-Taghabun", "At-Talaq",
    "At-Tahrim", "Al-Mulk", "Al-Qalam", "Al-Haqqa", "Al-Ma'arij", "Nuh", "Al-Jinn",
    "Al-Muzzammil", "Al-Muddaththir", "Al-Qiyama", "Al-Insan", "Al-Mursalat", "An-Naba",
    "An-Nazi'at", "Abasa", "At-Takwir", "Al-Infitar", "Al-Mutaffifin", "Al-Inshiqaq",
    "Al-Buruj", "At-Tariq", "Al-A'la", "Al-Ghashiya", "Al-Fajr", "Al-Balad", "Ash-Shams",
    "Al-Lail", "Ad-Duhaa", "Ash-Sharh", "At-Tin", "Al-Alaq", "Al-Qadr", "Al-Bayyina",
    "Az-Zalzala", "Al-Adiyat", "Al-Qari'a", "At-Takathur", "Al-Asr", "Al-Humaza", "Al-Fil",
    "Quraysh", "Al-Ma'un", "Al-Kawthar", "Al-Kafirun", "An-Nasr", "Al-Masad", "Al-Ikhlas",
    "Al-Falaq", "An-Nas"
  ];

  useEffect(() => {
    const userId = localStorage.getItem('user_id');
    if (!userId) {
      window.location.href = '/login';
      return;
    }

    fetch(`http://localhost:5000/main?user_id=${userId}`)
      .then((response) => response.json())
      .then((data) => {
        setUserData(data);
        const progress = data.ayahs_read / data.ayahs_per_day || 0;
        setProgressPercentage(Math.min(progress * 100, 100));
      })
      .catch((error) => console.error('Error fetching user data:', error));
  }, []);

  const handleGoalSubmit = (e) => {
    e.preventDefault();
    const userId = localStorage.getItem('user_id');
    fetch('http://localhost:5000/goals', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ayahs_per_day: parseInt(newGoal, 10), user_id: userId }),
    })
      .then(() => window.location.reload())
      .catch((error) => console.error('Error setting goal:', error));
  };

  const handleProgressUpdate = (e) => {
    e.preventDefault();
    const userId = localStorage.getItem('user_id');
    fetch('http://localhost:5000/progress', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ayahs_read: parseInt(ayahsRead, 10), user_id: userId }),
    })
      .then(() => window.location.reload())
      .catch((error) => console.error('Error updating progress:', error));
  };

  return (
    <div className="main-content">
      {/* Search Bar */}
      <div className="search-bar">
        <input type="text" placeholder="Search..." className="search-input" />
        <button className="search-button">üîç</button>
      </div>

      {/* Progress Section */}
      <div className="progress-section">
        <h2>Progress</h2>
        {userData && (
          <>
            <p>{`Goal: ${userData.ayahs_per_day || 'N/A'} ayahs/day`}</p>
            <div className="progress-bar">
              <div
                className="progress-bar-fill"
                style={{ width: `${progressPercentage}%` }}
              ></div>
            </div>
          </>
        )}
        <form onSubmit={handleGoalSubmit} className="goal-form">
          <input
            type="number"
            placeholder="Set Goal (Ayahs/day)"
            value={newGoal}
            onChange={(e) => setNewGoal(e.target.value)}
          />
          <button type="submit">Set Goal</button>
        </form>
        <form onSubmit={handleProgressUpdate} className="progress-form">
          <input
            type="number"
            placeholder="Update Ayahs Read"
            value={ayahsRead}
            onChange={(e) => setAyahsRead(e.target.value)}
          />
          <button type="submit">Update Progress</button>
        </form>
      </div>

      {/* Surah Section */}
      <div className="surah-cards">
        {surahNames.map((name, i) => (
          <Link key={i + 1} to={`/surah/${i + 1}`} className="surah-card">
            {name}
          </Link>
        ))}
      </div>
    </div>
  );
};

export default MainContent;
